mapper.select:
SELECT
    *
FROM
    material
WHERE
    material_id = #{mId}
  AND material_name = #{mName}
  AND material_type_id = #{mTypeId}
  AND state_id = 2

SELECT
    *
FROM
    material
WHERE
    material_id = #{mId}
  AND material_name = #{mName}
  AND material_type_id = #{mTypeId}
  AND state_id = 0

SELECT m1.material_id mId, m1.material_name mName, m1.material_type_id mTypeId, m1.material_number mNumber, m1.state_id mState, m3.state_name mStateName, m2.material_type_desc mTypeDesc FROM material m1, material_type m2, material_state m3 WHERE m1.MATERIAL_TYPE_ID = m2.MATERIAL_TYPE_ID AND m1.STATE_ID = m3.STATE_ID AND material_id in
#{mId}
AND m1.state_id = 0 ORDER BY m1.material_id

mapper.update:
update material set material_number = material_number - #{selectNumber}
where material_id in

    #{mId}

and state_id = 0

mapper.select:
SELECT * FROM material WHERE material_id IN
#{mId}
AND state_id = 1

mapper.update:
update material set material_number = material_number + #{selectNumber}
where material_id in

    #{mId}

and state_id = 1
